var currentTime = new Date();

var gd = new GlideDate();
gd.setValue(currentTime);
gd.addSeconds(25200‬);

workflow.info(gd);
//current.enddate.setValue(gd.getByFormat("yyyy-MM-dd HH:mm:ss")); 


var x = current.u_date_create;
var gtime1 = new GlideDate();
gtime1.setValue(x);
var dur = GlideDate.subtract(gtime1, gd); 

current.totaldate.setValue(dur.getDisplayValue()); 
current.enddate.setValue(gd.getByFormat("yyyy-MM-dd HH:mm:ss")); 


workflow.info(dur.getDisplayValue());

